uble totalDistance; //create variable total distance","        ","        totalDistance=nCounts*wheelDiameter*PI/inchesPerFoot/feetPerMile; //create total distance equation","        ","        totalDistance=(int)(totalDistance*100)/100.0;//limit the number of digits given for total distacne","        ","        System.out.println(\"The distance was \"+totalDistance+\" miles and took \"+nSeconds/secondsPerMinute+\" minutes\");","        //Print the distance and time taken","        ","        double averageSpeed;//create variable average speed","        double totalTime;//create variable total time","        totalTime=nSeconds/secondsPerMinute/minutesPerHour;//create equation for total time using the seconds given earlier","        averageSpeed=totalDistance/totalTime;//create equation for average speed","        double cleanAverageSpeed; //create variable clean average speed","        cleanAverageSpeed=(int)(averageSpeed*1000)/1000.00;//limit the number of digits given for average speed","        ","        ","        System.out.println(\"The average mph was \"+cleanAverageSpeed+\"\");//print the average speed","        ","        ","        ","        ","        ","        ","    }","}"],"id":102}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":0,"column":0},"end":{"row":0,"column":0},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":0},"timestamp":1455226687089,"hash":"67452301efcdab8998badcfe10325476c3d2e1f0"}